DROP TABLE IF EXISTS `storage`;
CREATE TABLE `storage` (`name` varchar(255)    NOT NULL   COMMENT '存储策略名称' ,`mode` int    NULL   COMMENT '存储策略类型' ,`disposition` varchar(255)    NULL   COMMENT '存储策略配置' ,`remark` varchar(255)    NULL   COMMENT '备注' ,`id` bigint    NOT NULL   COMMENT '主键id' ,`creator_id` bigint    NULL   COMMENT '创建人id' ,`create_time` datetime    NULL   COMMENT '创建时间' ,`updater_id` bigint    NULL   COMMENT '更新人id' ,`update_time` datetime    NULL   COMMENT '更新時間' ,`update_count` int    NULL   COMMENT '更新次數' ,`is_deleted` int    NULL DEFAULT 0  COMMENT '是否删除' ,PRIMARY KEY (`id`),UNIQUE INDEX `auto_idx_storage_name`(`name` ) ) ;
DROP TABLE IF EXISTS `image`;
CREATE TABLE `image` (`name` varchar(255)    NOT NULL   COMMENT '图像名称' ,`alise_name` varchar(255)    NULL   COMMENT '图像别名' ,`file_path` varchar(255)    NULL   COMMENT '文件路径' ,`shooting_time` datetime    NULL   COMMENT '拍摄时间' ,`photographer_id` bigint    NULL   COMMENT '摄影师id' ,`model_id` bigint    NULL   COMMENT '模特id' ,`location` varchar(255)    NULL   COMMENT '拍摄地点' ,`storage_id` bigint    NULL   COMMENT '存储策略id' ,`id` bigint    NOT NULL   COMMENT '主键id' ,`creator_id` bigint    NULL   COMMENT '创建人id' ,`create_time` datetime    NULL   COMMENT '创建时间' ,`updater_id` bigint    NULL   COMMENT '更新人id' ,`update_time` datetime    NULL   COMMENT '更新時間' ,`update_count` int    NULL   COMMENT '更新次數' ,`is_deleted` int    NULL DEFAULT 0  COMMENT '是否删除' ,PRIMARY KEY (`id`),UNIQUE INDEX `auto_idx_image_name`(`name` ) ) ;
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (`name` varchar(255)    NOT NULL   COMMENT '用户名称' ,`image_url` varchar(255)    NULL   COMMENT '用户头像地址' ,`telephone` varchar(255)    NULL   COMMENT '用户电话号码' ,`email` varchar(255)    NULL   COMMENT '用户邮箱地址' ,`id` bigint    NOT NULL   COMMENT '主键id' ,`creator_id` bigint    NULL   COMMENT '创建人id' ,`create_time` datetime    NULL   COMMENT '创建时间' ,`updater_id` bigint    NULL   COMMENT '更新人id' ,`update_time` datetime    NULL   COMMENT '更新時間' ,`update_count` int    NULL   COMMENT '更新次數' ,`is_deleted` int    NULL DEFAULT 0  COMMENT '是否删除' ,PRIMARY KEY (`id`)) ;
